CREATE TABLE `%PREFIX%userSessions` ( `userAuth_uID` int(10) unsigned zerofill NOT NULL default '0000000000', `Identifier` varchar(32) default '00000000000000000000000000000000', `Stamp` datetime default NULL, `Address` varchar(16) default '0.0.0.0', `Host` varchar(128) default NULL, PRIMARY KEY  (`userAuth_uID`), KEY `%PREFIX%Sessions_FKIndex1` (`userAuth_uID`), CONSTRAINT `%PREFIX%userSessions_ibfk_1` FOREIGN KEY (`userAuth_uID`) REFERENCES `%PREFIX%userAuthorization` (`uID`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE `%PREFIX%userSettings` ( `tID` int(10) unsigned NOT NULL auto_increment, `userAuth_uID` int(10) unsigned zerofill NOT NULL default '0000000000', `Identifier` varchar(32) NOT NULL default '', `Value` varchar(128) default NULL, PRIMARY KEY  (`tID`,`userAuth_uID`), KEY `%PREFIX%Preferences_FKIndex1` (`userAuth_uID`), CONSTRAINT `%PREFIX%userSettings_ibfk_1` FOREIGN KEY (`userAuth_uID`) REFERENCES `%PREFIX%userAuthorization` (`uID`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=latin1;

CREATE TABLE `%PREFIX%ExampleCustomers` ( `Customer_PK` int(11) NOT NULL, `CustomerName` varchar(50) NOT NULL, `ContactLastName` varchar(50) NOT NULL, `ContactFirstName` varchar(50) NOT NULL, `Phone` varchar(50) NOT NULL, `AddressLine1` varchar(50) NOT NULL, `AddressLine2` varchar(50) DEFAULT NULL, `City` varchar(50) NOT NULL, `State` varchar(50) DEFAULT NULL, `PostalCode` varchar(15) DEFAULT NULL, `Country` varchar(50) NOT NULL, `SalesRep_Employee_FK` int(11) DEFAULT NULL, `CreditLimit` double DEFAULT NULL, PRIMARY KEY (`Customer_PK`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
